import React, { useEffect, useState } from "react";
import "./App.css";

const API_BASE = "http://localhost:8000/api";

function BookTracker() {
  const [books, setBooks] = useState([]);
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [stats, setStats] = useState({});
  const [error, setError] = useState("");
  const [showAddBook, setShowAddBook] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const [bookForm, setBookForm] = useState({
    title: "",
    author: "",
    status: "want_to_read",
    pages: "",
    current_page: "",
    rating: "",
    notes: "",
  });

  const [recommendations, setRecommendations] = useState([]);
  const [aiPrompt, setAiPrompt] = useState("");
  const [loadingRecommendations, setLoadingRecommendations] = useState(false);
  const [showRecommendations, setShowRecommendations] = useState(false);

  // --- API CALL HELPER ---
  const api = async (path, options = {}) => {
    const res = await fetch(`${API_BASE}${path}`, {
      ...options,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
        ...(options.headers || {}),
      },
    });
    if (!res.ok) {
      const text = await res.text();
      throw new Error(`API error: ${res.status} - ${text}`);
    }
    return res.json();
  };

  // --- LOAD BOOKS & STATS ---
  useEffect(() => {
    if (!token) return;
    loadBooks();
    loadStats();
  }, [token]);

  const loadBooks = async () => {
    try {
      const data = await api("/books");
      setBooks(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load books.");
    }
  };

  const loadStats = async () => {
    try {
      const data = await api("/stats");
      setStats(data);
    } catch (err) {
      console.error(err);
      setError("Failed to load stats.");
    }
  };

  // --- ADD OR EDIT BOOK ---
  const handleBookSubmit = async (e) => {
    e.preventDefault();
    try {
      const method = editingBook ? "PUT" : "POST";
      const path = editingBook ? `/books/${editingBook.id}` : "/books";
      await api(path, {
        method,
        body: JSON.stringify(bookForm),
      });
      setShowAddBook(false);
      setEditingBook(null);
      setBookForm({
        title: "",
        author: "",
        status: "want_to_read",
        pages: "",
        current_page: "",
        rating: "",
        notes: "",
      });
      loadBooks();
      loadStats();
    } catch (err) {
      console.error(err);
      setError("Failed to save book.");
    }
  };

  const deleteBook = async (id) => {
    if (!window.confirm("Delete this book?")) return;
    try {
      await api(`/books/${id}`, { method: "DELETE" });
      loadBooks();
      loadStats();
    } catch (err) {
      console.error(err);
      setError("Failed to delete book.");
    }
  };

  // --- GEMINI AI RECOMMENDATION ---
  const fetchRecommendations = async (query) => {
    try {
      setLoadingRecommendations(true);
      setShowRecommendations(true);
      const data = await api("/recommendations", {
        method: "POST",
        body: JSON.stringify({ prompt: query }),
      });

      // Handle both JSON and text
      if (Array.isArray(data.recommendations)) {
        setRecommendations(data.recommendations);
      } else if (typeof data.recommendations === "string") {
        const lines = data.recommendations.split("\n").filter((l) => l.trim() !== "");
        setRecommendations(lines.map((line) => ({ title: line })));
      } else {
        setRecommendations([]);
      }
    } catch (err) {
      console.error(err);
      setError("AI recommendation failed.");
    } finally {
      setLoadingRecommendations(false);
    }
  };

  // --- SAVE AI RECOMMENDED BOOK ---
  const saveRecommendation = async (rec) => {
    try {
      await api("/books", {
        method: "POST",
        body: JSON.stringify({
          title: rec.title,
          author: rec.author,
          notes: rec.description,
          status: "want_to_read",
        }),
      });
      alert(`Saved "${rec.title}" to your library!`);
      loadBooks();
    } catch (err) {
      console.error(err);
      setError("Failed to save recommended book.");
    }
  };

  return (
    <div className="app-container">
      <h1 className="app-title">📚 Book Tracker with AI Recommendations</h1>

      {/* === AI RECOMMENDATION SECTION === */}
      <div className="ai-section">
        <h2>AI Book Recommendations</h2>
        <div className="ai-form">
          <input
            type="text"
            value={aiPrompt}
            onChange={(e) => setAiPrompt(e.target.value)}
            placeholder="Ask AI... e.g. 'Recommend fantasy books like Harry Potter'"
            className="ai-input"
          />
          <button
            className="btn-submit"
            onClick={() => fetchRecommendations(aiPrompt)}
            disabled={!aiPrompt || loadingRecommendations}
          >
            {loadingRecommendations ? "Loading..." : "Get Recommendations"}
          </button>
        </div>

        {showRecommendations && (
          <div className="recommendation-results">
            {loadingRecommendations ? (
              <p>Loading AI suggestions...</p>
            ) : recommendations.length > 0 ? (
              recommendations.map((r, i) => (
                <div key={i} className="recommendation-card">
                  <div className="recommendation-title">{r.title || "Untitled"}</div>
                  {r.author && <div className="recommendation-author">by {r.author}</div>}
                  {r.description && (
                    <div className="recommendation-description">{r.description}</div>
                  )}
                  <button
                    className="btn-secondary"
                    onClick={() => saveRecommendation(r)}
                  >
                    Save to My Books
                  </button>
                </div>
              ))
            ) : (
              <p>No recommendations yet. Try asking AI for ideas!</p>
            )}
          </div>
        )}
      </div>

      {/* === STATS SECTION === */}
      <div className="stats">
        <h2>Your Reading Stats</h2>
        <p>Total Books: {stats.total_books || 0}</p>
        <p>Completed: {stats.completed || 0}</p>
        <p>Reading: {stats.reading || 0}</p>
        <p>Want to Read: {stats.want_to_read || 0}</p>
        <p>Pages Read: {stats.total_pages_read || 0}</p>
        <p>Average Rating: {stats.average_rating || 0}</p>
      </div>

      {/* === BOOKS LIST === */}
      <div className="books-section">
        <h2>My Books</h2>
        <button className="btn-submit" onClick={() => setShowAddBook(true)}>
          + Add Book
        </button>

        <div className="book-list">
          {books.length === 0 ? (
            <p>No books yet. Start adding some!</p>
          ) : (
            books.map((b) => (
              <div key={b.id} className="book-card">
                <div className="book-title">{b.title}</div>
                <div className="book-author">{b.author}</div>
                <div className="book-status">Status: {b.status}</div>
                {b.rating && <div>Rating: ⭐ {b.rating}</div>}
                <div className="book-actions">
                  <button
                    className="btn-secondary"
                    onClick={() => {
                      setEditingBook(b);
                      setBookForm(b);
                      setShowAddBook(true);
                    }}
                  >
                    Edit
                  </button>
                  <button className="btn-delete" onClick={() => deleteBook(b.id)}>
                    Delete
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* === ADD / EDIT BOOK MODAL === */}
      {showAddBook && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingBook ? "Edit Book" : "Add Book"}</h2>
            <form onSubmit={handleBookSubmit}>
              <div className="form-group">
                <label>Title</label>
                <input
                  type="text"
                  value={bookForm.title}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, title: e.target.value })
                  }
                  required
                />
              </div>

              <div className="form-group">
                <label>Author</label>
                <input
                  type="text"
                  value={bookForm.author}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, author: e.target.value })
                  }
                />
              </div>

              <div className="form-group">
                <label>Status</label>
                <select
                  value={bookForm.status}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, status: e.target.value })
                  }
                >
                  <option value="want_to_read">Want to Read</option>
                  <option value="reading">Reading</option>
                  <option value="completed">Completed</option>
                </select>
              </div>

              <div className="form-group">
                <label>Pages</label>
                <input
                  type="number"
                  value={bookForm.pages}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, pages: e.target.value })
                  }
                />
              </div>

              <div className="form-group">
                <label>Current Page</label>
                <input
                  type="number"
                  value={bookForm.current_page}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, current_page: e.target.value })
                  }
                />
              </div>

              <div className="form-group">
                <label>Rating (1-5)</label>
                <input
                  type="number"
                  value={bookForm.rating}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, rating: e.target.value })
                  }
                  min="1"
                  max="5"
                />
              </div>

              <div className="form-group">
                <label>Notes</label>
                <textarea
                  value={bookForm.notes}
                  onChange={(e) =>
                    setBookForm({ ...bookForm, notes: e.target.value })
                  }
                />
              </div>

              <div className="modal-actions">
                <button
                  type="button"
                  onClick={() => {
                    setShowAddBook(false);
                    setEditingBook(null);
                  }}
                  className="btn-secondary"
                >
                  Cancel
                </button>
                <button type="submit" className="btn-submit">
                  {editingBook ? "Update" : "Add"} Book
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {error && <div className="error-message">{error}</div>}
    </div>
  );
}

export default BookTracker;
